import React from "react";

class Users extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      users: [
        {
          id: 1,
          firstname: 'Bob',
          lastname: 'Marley',
          bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Distinctio, voluptas.',
          age: 40,
          isHappy: true
        },
        {
          id: 2,
          firstname: 'John',
          lastname: 'Doe',
          bio: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Distinctio, voluptas.',
          age: 22,
          isHappy: false
        }
      ]
    }
  }

  render() {
    if (this.users.length !== 0)
      return (
        <div className="users__list">
          {
            this.users.map((el) => (
              <div className="item" key={el.id}>
                <h3>{thiscel.firstname} {el.lastname}</h3>
                <p>{el.bio}</p>
                <b>{el.isHappy ? "Счастлив :)" : "Не счастлив :("}</b>
              </div>
            ))
          }
        </div>
      )
    else
      return (
        <div className="users__list">
          <div className="item">
            <h3>Пользователей нет</h3>
          </div>
        </div>
      )
  }
}

export default Users
