import React from "react";

class User extends React.Component {
  user = this.props.user
  render() {
    return (
      <div className="item">
        <h3>{this.user.firstname} {this.user.lastname}</h3>
        <p>{this.user.bio}</p>
        <b>{this.user.isHappy ? "Счастлив :)" : "Не счастлив :("}</b>
      </div>
    )
  }
}

export default User
